 window.onload = function(){
    document.querySelector('#btnMessages').addEventListener('click', sendMessaje)
    document.querySelector('#message').addEventListener('keypress',
    function(e){
        if(e.keyCode == 13){
            sendMessaje()
        }
    }
    )


    function sendMessaje(){
        var message = document.querySelector('#message')
        
        loadMessajeHTML(message.value)

        if(message.value != ''){
            message.value = ''
        }
    }

    function loadMessajeHTML(m){
        
        document.querySelector('#boxMenssages').innerHTML +=
        `
        <div class="alert alert-primary" role="alert">
            ${m}
            <div>
                <small class="fst-italic fw-bold">
                    ${user}
                </small>
                <small class="float-end"></small>
            </div>
        </div>
        `
    }
 }